  const [principal, setPrincipal] = useState<number>();

  const upadtePrincipal = (val:number) => {
    setPrincipal((val) => {
      console.log(val);
      return val;
    });
  }

  return (
    <div>
      <input type="text" onChange={(ev)=> {
        upadtePrincipal(Number(ev.target.value));
      }}></input>

      Principal Balance: {principal}
    </div>
  );
}

export default App;
