import React, {useState, useEffect, useCallback} from "react";
import Dice from "./components/Dice";

import './App.css';
import { pmt } from 'financial'

// Usage
function App() {

  const [principal, setPrincipal] = useState<number>(740000);
  const [rate, setRate] = useState<number>(2.875);
  const [term, setTerm] = useState<number>(30);
  const [payment, setPayment] = useState<number>(0);


  useEffect(() => {
    // Update the document title using the browser API
    console.log(rate);
    console.log(term*12);
    console.log(principal);
    let myPayment = pmt((rate/12), (term*12), (-1*principal));
    console.log(pmt(rate/12, 12*15, 200000));
    setPayment(myPayment);
  });

  return (
    <div>
      <label htmlFor="principal">Principal Balance ($): </label>
      <input type="text" name="principal" value={principal} onChange={(ev)=> {
        setPrincipal(Number(ev.target.value));
      }}></input>
      <label htmlFor="rate">Rate (%): </label>
      <input type="text" name="rate" value={rate} onChange={(ev)=> {
        setRate(Number(ev.target.value));
      }}></input>
      <label htmlFor="term">Term (years): </label>
      <input type="text" name="term" value={term} onChange={(ev)=> {
        setTerm(Number(ev.target.value));
      }}></input>

      Payment: {payment}
    </div>
  );
}

export default App;
