import React, {useState} from "react";
import Dice from "./components/Dice";

import './App.css';

// Usage
function App() {
  const [numDice, setNum] = useState(5);
  const [update, setUpdate] = useState(false);

  const items = [];

  for(let i = 0;  i< numDice; i++) {
    items.push(<Dice update={update}/>);
  }

  return (
    <div>
      {items}
      <button onClick={() => {
        setUpdate(update => !update);
      }}>
      roll
      </button>
    </div>
  );
}

export default App;
