import React, {useState, useEffect} from "react";
import Dice from "./components/Dice";

import './App.css';

// Usage
function App() {
  const numDice = 5;

  const [value, setValue] = useState<number[]>([]);
  const [enabled, setEnabled] = useState<boolean[]>([]);

  const updateDice = (itemRef:number) => {
    setValue(() => {
      let items = value;
      items[itemRef] = Math.floor(Math.random() * 6)+1;
      return items;
    });
  }

  const enableDice = (itemRef:number) => {
    setEnabled(()=> {
      let items = enabled;
      items[itemRef] = !enabled[itemRef];
      return items;
    });

  }

  useEffect(() => {
    setValue(()=>{
      let items = [];
      for(let i = 0;  i< numDice; i++) {
        items.push(Math.floor(Math.random() * 6)+1);
      }
      return items;
    });
    setEnabled(()=>{
      let items = [];
      for(let i = 0;  i< numDice; i++) {
        items.push(false);
      }
      return items;
    })
  }, [numDice]);

  return (
    <div>
      {
        enabled.map((en,i)=>{
          return <Dice value={value[i]} enabled={en} updateDice={updateDice} setIsTextChanged={enableDice} arrayRef={i} key={i}></Dice>
        })
      }
      <button onClick={() => {
      }}>
      roll
      </button>
    </div>
  );
}

export default App;
