import React, {useState, useEffect, useCallback} from "react";
import Dice from "./components/Dice";

import './App.css';

// Usage
function App() {
  const numDice = 5;

  const [value, setValue] = useState<number[]>([]);
  const [enabled, setEnabled] = useState<boolean[]>([]);

  // Define and memorize toggler function in case we pass down the comopnent,
  // This function change the boolean value to it's opposite value
  const toggle = useCallback((i :number): void => {
    console.log(enabled);
    let en = enabled;
    en[i] = !enabled[i];
    setEnabled(en)
  }, []);

  const upadteDice = (itemRef:number) => {
    setValue(() => {
      let items = value;
      items[itemRef] = Math.floor(Math.random() * 6)+1;
      return items;
    });
  }

  const enableDice = (itemRef:number) => {
    setEnabled(()=> {
      console.log("EnableDice")
      let items = enabled;
      items[itemRef] = !enabled[itemRef];
      return items;
    });

  }

  useEffect(() => {
    setValue(()=>{
      let items = [];
      for(let i = 0;  i< numDice; i++) {
        items.push(Math.floor(Math.random() * 6)+1);
      }
      return items;
    });
    setEnabled(()=>{
      let items = [];
      for(let i = 0;  i< numDice; i++) {
        items.push(false);
      }
      return items;
    })
  }, [numDice]);

  return (
    <div>
      {
        value.map((val,i)=>{
          return <Dice value={val} enabled={enabled[i]} updateDice={upadteDice} setIsTextChanged={toggle} arrayRef={i} key={i}></Dice>
        })
      }
      <button onClick={() => {
       console.log("here", enabled)
      }}>
      roll
      </button>
    </div>
  );
}

export default App;
