import React, {useState, useEffect} from "react";

import './App.css';
import { pmt } from 'financial'

// Usage
function App() {

  const [principal, setPrincipal] = useState<number>(200000);
  const [rate, setRate] = useState<number>(5.75);
  const [term, setTerm] = useState<number>(30);
  const [other, setOther] = useState<number>(500);
  const [pi, setPI] = useState<number>(1167.15);
  const [taxes, setTaxes] = useState<number>(166.67);
  const [pmi, setPMI] = useState<number>(20.00);
  const [insurance, setInsurance] = useState<number>(83.33);
  const [debts, setDebts] = useState<number>(0);
  const [income, setIncome] = useState<number>(6000);
  const [payment, setPayment] = useState<number>(0);
  const [hRatio, setRatio] = useState<number>(0);
  const [dti, setDTI] = useState<number>(0);


  useEffect(() => {
    let myPayment = pmt(((rate/100)/12), (term*12), -principal);
    setPayment(Math.round(myPayment * 100)/100);
    setDebts(pi+taxes+pmi+insurance);
    let myHRatio =  debts/income;
    setRatio(Math.round(myHRatio*10000)/100);
    let myDTI =  (debts+other)/income;
    setDTI(Math.round(myDTI*10000)/100);
  });

  return (
    <div>
      <ul>
        <li>
          <label htmlFor="principal">Principal Balance ($): </label>
          <input type="text" name="principal" value={principal} onChange={(ev)=> {
            setPrincipal(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="rate">Rate (%): </label>
          <input type="text" name="rate" value={rate} onChange={(ev)=> {
            setRate(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="term">Term (years): </label>
          <input type="text" name="term" value={term} onChange={(ev)=> {
            setTerm(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="other">Other ($): </label>
          <input type="text" name="other" value={other} onChange={(ev)=> {
            setOther(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="debts">P+I ($): </label>
          <input type="text" name="pi" value={pi} onChange={(ev)=> {
            setPI(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="taxes">Taxes ($): </label>
          <input type="text" name="taxes" value={taxes} onChange={(ev)=> {
            setTaxes(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="pmi">PMI ($): </label>
          <input type="text" name="pmi" value={pmi} onChange={(ev)=> {
            setPMI(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="insurance">Insurance ($): </label>
          <input type="text" name="insurance" value={insurance} onChange={(ev)=> {
            setInsurance(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="income">Income ($ annual): </label>
          <input type="text" name="income" value={income} onChange={(ev)=> {
            setIncome(Number(ev.target.value)/12);
          }}></input>
        </li>
      </ul>

      <p>
        Payment: {payment}
      </p>
      <p>
        Housing Ratio: {hRatio}
      </p>
      <p>
        Total Debt Ratio: {dti}
      </p>
    </div>
  );
}

export default App;
