import React, {useState, useEffect, useCallback} from "react";
import Dice from "./components/Dice";

import './App.css';
import { pmt } from 'financial'

// Usage
function App() {

  const [principal, setPrincipal] = useState<number>(0);
  const [rate, setRate] = useState<number>(0);
  const [term, setTerm] = useState<number>(0);
  const [payment, setPayment] = useState<number>(0);


  useEffect(() => {
    // Update the document title using the browser API
    let myPayment = pmt(rate, term, -principal);
    setPayment(myPayment);
  });

  return (
    <div>
      <label htmlFor="principal">Principal Balance: </label>
      <input type="text" name="principal" onChange={(ev)=> {
        setPrincipal(Number(ev.target.value));
      }}></input>
      <label htmlFor="principal">Rate: </label>
      <input type="text" name="principal" onChange={(ev)=> {
        setRate(Number(ev.target.value));
      }}></input>
      <label htmlFor="principal">Term: </label>
      <input type="text" name="principal" onChange={(ev)=> {
        setTerm(Number(ev.target.value));
      }}></input>

      Payment: {payment}
    </div>
  );
}

export default App;
