import React, {useState, useEffect} from "react";

import './App.css';
import '../node_modules/rsuite/dist/rsuite.min.css';
//import { pmt } from 'financial'
import { Slider, RangeSlider } from 'rsuite';

// Usage
function App() {

  const [principal, setPrincipal] = useState<number>(50020);
  const [rate, setRate] = useState<number>(5.75);
  const [term, setTerm] = useState<number>(30);
  const [other, setOther] = useState<number>(500);
  const [pi, setPI] = useState<number>(1167.15);
  const [taxes, setTaxes] = useState<number>(166.67);
  const [pmi, setPMI] = useState<number>(20.00);
  const [insurance, setInsurance] = useState<number>(83.33);
  const [debts, setDebts] = useState<number>(0);
  const [income, setIncome] = useState<number>(72000);
  const [payment, setPayment] = useState<number>(0);
  const [hRatio, setRatio] = useState<number>(0);
  const [dti, setDTI] = useState<number>(36);

  const maxAffordability = 0.28;
  //const maxdti = useState<number>(36);

  let fieldsValidation = [true]

  function validateIsCurrency(val :string, index :number) {
    fieldsValidation[index] = /(?=.*?\d)^\$?(([1-9]\d{0,2}(,\d{3})*)|\d+)?(\.\d{1,2})?$/.test(val);
    return fieldsValidation[index];
  }

  useEffect(() => {

    let myPayment = (income/12)*maxAffordability;

    setPayment(Math.round(myPayment * 100)/100);


    let i = (rate/100)/12;
    let n = term*12;
    let x = payment / (( i * (1 + i)**n ) / ( (1 + i)**n - 1));

    setPrincipal(Math.round(x * 100)/100);

    setDebts(pi+taxes+pmi+insurance);
    let myHRatio =  debts/(income/12);
    setRatio(Math.round(myHRatio*10000)/100);
    let myDTI =  (debts+other)/(income/12);
    setDTI(Math.round(myDTI*10000)/100);
  }, [principal, rate, term, payment, debts, income, other, hRatio, dti, insurance, pi, taxes, pmi]);

  return (
    <div>
      <ul>
        <li>
          <label htmlFor="income">Income ($ annual): </label>
          <label htmlFor="income" className={fieldsValidation[0]?"error hidden":"error shown"}>Error message</label>
          <input type="tel" name="income" value={income} onChange={(ev)=> {

            if (validateIsCurrency(ev.target.value,0)) {
              setIncome(Number(ev.target.value));
            }
          }}></input>
        </li>
        <li>
          <label htmlFor="rate">Rate (%): </label>
          <input type="tel" name="rate" value={rate} onChange={(ev)=> {
            setRate(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="term">Term (years): </label>
          <input type="tel" name="term" value={term} onChange={(ev)=> {
            setTerm(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="other">Other ($): </label>
          <input type="tel" name="other" value={other} onChange={(ev)=> {
            setOther(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="debts">P+I ($): </label>
          <input type="tel" name="pi" value={pi} onChange={(ev)=> {
            setPI(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="taxes">Taxes ($): </label>
          <input type="tel" name="taxes" value={taxes} onChange={(ev)=> {
            setTaxes(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="pmi">PMI ($): </label>
          <input type="tel" name="pmi" value={pmi} onChange={(ev)=> {
            setPMI(Number(ev.target.value));
          }}></input>
        </li>
        <li>
          <label htmlFor="insurance">Insurance ($): </label>
          <input type="tel" name="insurance" value={insurance} onChange={(ev)=> {
            setInsurance(Number(ev.target.value));
          }}></input>
        </li>
      </ul>

      <p>
        Payment: {payment}
      </p>
      <p>
        Principal: {principal}
      </p>
      <p>
        Housing Ratio: {hRatio}
      </p>
      <p>
        Total Debt Ratio: {dti}
      </p>
    </div>
  );
}

export default App;
