import React, {useState, useEffect} from "react";
import Dice from "./components/Dice";

import './App.css';

// Usage
function App() {
  const [numDice, setNum] = useState(5);

  const [value, setValue] = useState<number[]>([]);

  const updateDice = (itemRef:number) => {
    setValue(() => {
      let items = value;
      items[itemRef] = Math.floor(Math.random() * 6)+1;
      return items;
    });
  }

  useEffect(() => {
    setValue(()=>{
      let items = [];
      for(let i = 0;  i< numDice; i++) {
        items.push(Math.floor(Math.random() * 6)+1);
      }
      return items;
    });
  }, [numDice]);

  return (
    <div>
      {
        value.map((val,i)=>{
          return <Dice value={val} arrayRef={i} updateDice={updateDice} key={i}></Dice>
        })
      }
      <button onClick={() => {
      }}>
      roll
      </button>
    </div>
  );
}

export default App;
